import unittest
from your_module import sum_two_values

class TestSumTwoValues(unittest.TestCase):
    def test_add_positive_numbers(self):
        self.assertEqual(sum_two_values(5, 3), 8)

    def test_add_negative_numbers(self):
        self.assertEqual(sum_two_values(-2, 7), 5)

    def test_add_zero(self):
        self.assertEqual(sum_two_values(10, 0), 10)

    def test_add_float_numbers(self):
        self.assertEqual(sum_two_values(3.5, 2.5), 6.0)
