import unittest
from your_module import login

class TestLogin(unittest.TestCase):

    def test_valid_credentials(self):
        result = login('admin', 'password123')
        self.assertEqual(result, 'Login successful!')

    def test_invalid_credentials(self):
        result = login('user123', 'pass456')
        self.assertEqual(result, 'Invalid username or password.')

    def test_empty_credentials(self):
        result = login('', '')
        self.assertEqual(result, 'Invalid username or password.')

import unittest
from sum_two_values import sum_two_values

class TestSumTwoValues(unittest.TestCase):

    def test_add_positive_numbers(self):
        self.assertEqual(sum_two_values(5, 3), 8)

    def test_add_negative_numbers(self):
        self.assertEqual(sum_two_values(-2, 7), 5)

    def test_add_zero(self):
        self.assertEqual(sum_two_values(10, 0), 10)

    def test_add_float_numbers(self):
        self.assertEqual(sum_two_values(3.5, 2.5), 6.0)

    def test_add_large_numbers(self):
        self.assertEqual(sum_two_values(1000000000, 2000000000), 3000000000)

if __name__ == '__main__':
    unittest.main()
